-- Multi-Tenant Database Schema Extensions\n\n-- Table to store tenant information\nCREATE TABLE tenants (\n    id SERIAL PRIMARY KEY,\n    name VARCHAR(255) NOT NULL,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Table to store subscription plans\nCREATE TABLE subscription_plans (\n    id SERIAL PRIMARY KEY,\n    name VARCHAR(255) NOT NULL,\n    price DECIMAL(10, 2) NOT NULL,\n    features JSONB,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Table to track usage per tenant\nCREATE TABLE usage_tracking (\n    id SERIAL PRIMARY KEY,\n    tenant_id INT REFERENCES tenants(id),\n    usage_date DATE NOT NULL,\n    usage_count INT DEFAULT 0,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Indexes for performance optimization\nCREATE INDEX idx_tenant_name ON tenants(name);\nCREATE INDEX idx_usage_tracking_tenant_id ON usage_tracking(tenant_id);\nCREATE INDEX idx_usage_tracking_date ON usage_tracking(usage_date);